mapper.insert:
INSERT INTO actingRole SET regDate = NOW(), updateDate = NOW(), recruitmentId = #{recruitId}, realName = #{roleRealName}, `name` = #{roleName}, pay = #{pay}, age = #{roleAge}, job = #{roleJob}, gender = #{roleGender}, scriptStatus = #{roleScript}, scenesCount = #{roleScenesCount}, shootingsCount = #{roleShootingsCount}
, `character` = #{roleCharacter}
, etc = #{actingRoleEtc}

mapper.update:
UPDATE actingRole


		updateDate = NOW()


		, realName = #{roleRealName}


		, `name` = #{roleName}


		, pay = #{pay}


		, age = #{roleAge}


		, gender = #{roleGender}


		, job = #{roleJob}


		, scriptStatus = #{roleScript}


		, scenesCount = #{roleScenesCount}


		, shootingsCount = #{roleShootingsCount}


		, `character` = #{roleCharacter}


		, `etc` = #{actingRoleEtc}


WHERE recruitmentId = #{recruitmentId}
