mapper.update:
UPDATE ap

		updateDate = NOW(),
		loginPw = #{loginPw}

	WHERE loginId = #{loginId}
	AND loginProviderTypeCode = 'ap'
	AND delStatus != 1

mapper.select:
SELECT * FROM ap
WHERE authKey = #{authKey}
AND delStatus != 1

SELECT * FROM ap
WHERE id = #{loginedMemberId}
AND delStatus != 1

SELECT COUNT(*) FROM `like`
WHERE relId = #{relId}
AND memberId = #{memberId}

mapper.delete:
DELETE FROM `like`
WHERE relId = #{relId}
AND memberId = #{memberId}

mapper.update:
UPDATE ap
delDate = NOW(), delStatus = 1
WHERE id = #{memberId}

mapper.select:
SELECT * FROM ap
WHERE name = #{name}
AND regNumber = #{regNumber}
AND loginProviderTypeCode = 'ap'
AND delStatus != 1

SELECT * FROM ap
WHERE loginProviderTypeCode = #{loginProviderTypeCode}
AND onLoginProviderMemberId = #{onLoginProviderMemberId}
