mapper.select:
SELECT * FROM application
WHERE delStatus != 1
AND recruitId = #{id}

SELECT * FROM application
WHERE delStatus != 1
AND memberId = #{memberId}

mapper.insert:
INSERT INTO `like` SET
regDate = NOW(), updateDate = NOW()
, relTypeCode = #{relTypeCode}
, relId = #{applicationId}
, memberTypeCode = 'pd'
, memberId = #{memberId}

mapper.select:
SELECT COUNT(*) FROM `like`
WHERE relTypeCode = #{relTypeCode}
AND relId = ${applicationId}
AND memberTypeCode = #{memberTypeCode}
AND memberId = #{memberId}

SELECT apl.*, ap.name AS extra__ap__name FROM application AS apl
INNER JOIN ap AS ap
ON apl.memberId = ap.id
INNER JOIN artwork AS artwork
ON artwork.relTypeCode = 'recruitment'
AND artwork.relId = #{recruitId}
INNER JOIN actingRole as actingRole
ON actingRole.recruitmentId = #{recruitId}
WHERE apl.recruitId = #{recruitId}
AND apl.delStatus != 1

mapper.delete:
DELETE FROM `like`
WHERE relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND memberTypeCode = #{memberTypeCode}
AND memberId = #{memberId}

mapper.select:
SELECT * FROM application
WHERE delStatus != 1
AND id = #{id}
