mapper.select:
SELECT * FROM attr
WHERE relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND (expireDate >= NOW() OR expireDate IS NULL)

SELECT `value` FROM attr
WHERE relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND (expireDate >= NOW() OR expireDate IS NULL)

mapper.delete:
DELETE FROM attr
WHERE relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}

mapper.insert:
INSERT INTO attr SET regDate = NOW() , updateDate = NOW(),
expireDate = #{expireDate},
relTypeCode = #{relTypeCode}, relId = #{relId}, typeCode = #{typeCode}, type2Code = #{type2Code}, `value` = #{value}

