mapper.select:
SELECT * FROM pd
WHERE id = #{id}
AND authLevel = 1

mapper.update:
UPDATE pd

	updateDate = NOW(),
	loginPw = #{loginPw}

WHERE loginId = #{email}
AND delStatus != 1

UPDATE pd
delDate = NOW(), delStatus = 1
WHERE id = #{id}

mapper.select:
SELECT * FROM pd
WHERE `name` = #{name}
AND regNumber = #{regNumber}
