mapper.select:
SELECT * FROM recruitment
WHERE delStatus != 1
LIMIT #{limit}

mapper.update:
UPDATE recruitment


		updateDate = NOW()


		, title = #{rmTitle}


		, `body` = #{rmBody}


		, `roleType` = #{rmRoleType}


		, `pay` = #{rmPay}


		, `location` = #{rmLocation}


		, `period` = #{rmPeriod}


		, `deadline` = #{rmDeadline}


		, `gender` = #{rmGender}


		, `age` = #{rmAge}


		, `script` = #{rmScript}


		, `videoTime` = #{rmVideoTime}


		, `etc` = #{rmEtc}


WHERE id = #{recruitmentId} 
AND memberId = #{memberId}
